import React, { Component } from 'react';
// import './App.scss';
// import AppRoutes from './AppRoutes';
// import Navbar from './shared/Navbar';
// import Sidebar from './shared/Sidebar';
// import Footer from './shared/Footer';


class App extends Component {
  state = {}
  componentDidMount() {
    this.onRouteChanged();
  }
  render() {
    // let navbarComponent = !this.state.isFullPageLayout ? <Navbar /> : '';
    // let sidebarComponent = !this.state.isFullPageLayout ? <Sidebar /> : '';
    // let footerComponent = !this.state.isFullPageLayout ? <Footer /> : '';
    return (
      <div className="container-scroller">
        {/* {navbarComponent} */}
        <div className="container-fluid page-body-wrapper">
          {/* {sidebarComponent} */}
          <div className="main-panel">
            <div className="content-wrapper">
              {/* <AppRoutes /> */}
            </div>
            {/* {footerComponent} */}
          </div>
        </div>
      </div>
    );
  }

  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged();
    }
  }

  onRouteChanged() {
    console.log("ROUTE CHANGED");
    window.scrollTo(0, 0);
    const { i18n } = this.props;
    if (this.props.location.pathname === '/rtl') {
      document.querySelector('body').classList.add('rtl');
      i18n.changeLanguage('ar');
    } else {
      document.querySelector('body').classList.remove('rtl');
      i18n.changeLanguage('en');
    }
    const fullPageLayoutRoutes = ['/user-pages/login-1', '/user-pages/login-2', '/user-pages/register-1', '/user-pages/register-2', '/user-pages/lockscreen', '/error-pages/error-404', '/error-pages/error-500', '/general-pages/landing-page'];
    for (let i = 0; i < fullPageLayoutRoutes.length; i++) {
      if (this.props.location.pathname === fullPageLayoutRoutes[i]) {
        this.setState({
          isFullPageLayout: true
        })
        document.querySelector('.page-body-wrapper').classList.add('full-page-wrapper');
        break;
      } else {
        this.setState({
          isFullPageLayout: false
        })
        document.querySelector('.page-body-wrapper').classList.remove('full-page-wrapper');
      }
    }
  }

}

export default App;
